import React , { useEffect, useState }from 'react';
import Picture from './Picture';
import Button from '@material-ui/core/button';

const App = () => {

  const client_id=`cdf04b05665f0efd4eb7272c93633384cae55bb23a0833102eee61692ebda4fd`;

  const [images, setImages] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('mountains');

  useEffect(() =>{
    getImages();
  }, [query]);

  const updateSearch = e => {
    setSearch(e.target.value);
  }

  const getSearch = e => {
    e.preventDefault();
    setQuery(search);
    setSearch("")
  }

  const getImages = async () => {
    const response = await fetch (
      `https://api.unsplash.com/search/photos/?client_id=${client_id}&query=${query}`
    );
    const data = await response.json();
    setImages(data.results)
    console.log(data.results);
}


  return (
    <div className="App">  
      <form onSubmit={getSearch} className="search-form">
        <input className="search-bar" type="text" value={search} onChange={updateSearch}/>
        <button variant='contained' color='primary' className='search-button' type="submit">Search</button>
      </form>

          {images.map(image => (
            <Picture key={image.id} source={image.links.download}/>
          ) 
          )}    
    </div>
  );
}

export default App;
